  }


  return <tr>
      <td>{props.id_pedido}</td>
      <td>{props.cliente}</td>
      <td>{new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short'}).format(dt_pedido)}</td>
      <td>{props.status_descricao}</td>
      <td>{new Intl.NumberFormat('pt-BR', {style: 'currency', currency: 'BRL'}).format(props.vl_total)}</td>
      <td>
          <div className="dropdown">
              <button className="btn btn-outline-secondary dropdown-toggle" type="button" 
                      data-bs-toggle="dropdown" aria-expanded="false">
                  Opções
              </button>
              <ul className="dropdown-menu">
                  <li><Link className="dropdown-item" to={"/pedidos/editar/" + props.id_pedido}>Editar Pedido</Link></li>
                  <li><button className="dropdown-item" onClick={(e) => DeletarPedido(props.id_pedido)}>Excluir Pedido</button></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><button className="dropdown-item" onClick={(e) => AtualizarPedido(props.id_pedido, "F")}>Finalizar Pedido</button></li>
                  <li><button className="dropdown-item" onClick={(e) => AtualizarPedido(props.id_pedido, "A")}>Reabrir Pedido</button></li>
              </ul>
          </div>
      </td>
  </tr>
}

export default Pedido;
